INSERT INTO `news` VALUES ('113', 'PARTNERS', 'Logistics Insight Asia', 'http://www.logasiamag.com/', '<strong>Logistics Insight Asia</strong><br />\r\n<strong>Connecting the Supply Chain</strong><br />\r\nLogistics Insight Asia, published by Contineo Media, targets the needs of executives, managers, and engineers responsible for the various elements of their company’s logistics activities and infrastructure. It covers a wide scope of topics from supply chain strategies, software solutions, tracking technology, to handling and storage equipment.&nbsp;<br />\r\n<p>\r\n	Visit us for more information: http://www.logasiamag.com/&nbsp;\r\n</p>\r\n<div>\r\n	<br />\r\n</div>', '16', '0', '0', null, null, 'Logistics Insight Asia', '', 'Logistics Insight Asia', '2017-08-09 09:47:48');
INSERT INTO `news` VALUES ('115', 'PARTNERS', 'SupplyChainBrain', 'https://www.supplychainbrain.com', '<p class=\"MsoNormal\">\r\n	SupplyChainBrain, the world’s most\r\ncomprehensive supply chain management information resource, is accessed year\r\nround through a wide range of ever evolving multi-media formats by hundreds of\r\nthousands of senior level industry executives. In addition to addressing the\r\nfundamental principles of supply-chain management, SupplyChainBrain identifies\r\nemerging trends, technologies and best practices, forward thinking ideas and\r\ncutting-edge solutions-and continues to write and report about these as they\r\nevolve and mature.<o:p></o:p>\r\n</p>', '20', '0', '0', null, null, 'SupplyChainBrain', 'SupplyChainBrain', 'SupplyChainBrain', '2017-09-11 12:31:12');
INSERT INTO `news` VALUES ('116', 'INSIGHTS', '2017 VCI Supply Chain Forecasting & Planning Summit', 'VCIntegration', '<p>\r\n	<span style=\"white-space:normal;\"><span style=\"color:#333333;font-size:18px;\"><strong>&nbsp; &nbsp; &nbsp;Click the right button </strong></span><span style=\"color:#333333;font-size:18px;\"><strong>《Download Full Report》</strong></span><span style=\"color:#333333;font-size:18px;\"><strong>&nbsp;</strong></span></span><span style=\"white-space:normal;\"><download full=\"\" report=\"\"></download></span><span style=\"white-space:normal;\"><span style=\"color:#333333;font-size:18px;\"><strong>to view the </strong></span><span style=\"color:#333333;font-size:18px;\"><strong>P</strong></span><span style=\"color:#333333;font-size:18px;\"><strong>ost Event Report</strong></span><span style=\"color:#333333;font-size:18px;\"><strong> of VCI&nbsp;</strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"white-space:normal;font-size:18px;color:#333333;\"><span style=\"color:#333333;font-size:18px;\"><strong>&nbsp; &nbsp; &nbsp;Supply&nbsp;</strong></span></span><span style=\"font-size:18px;color:#333333;\"><span style=\"font-size:18px;color:#333333;\"><strong>Chain&nbsp;</strong></span></span><span style=\"font-size:18px;color:#333333;\"><strong>Forecasting &amp; Planning 2017！</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/upload/attached/image/20171024/20171024104157_335.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/upload/attached/image/20171024/20171024104211_757.png\" alt=\"\" /> \r\n</p>', '24', '0', '919', null, null, 'Post Event Report - 2017 VCI Supply Chain Forecasting & Planning', '', 'VCI; Planning; Forecasting; Demand Management; Report', '2017-10-24 09:39:03');
