-- ----------------------------
-- Table structure for news_pdf
-- ----------------------------
DROP TABLE IF EXISTS `news_pdf`;
CREATE TABLE `news_pdf` (
  `pid` bigint(32) NOT NULL AUTO_INCREMENT,
  `nid` bigint(32) DEFAULT NULL,
  `name` varchar(200) DEFAULT NULL,
  `cover_img` varchar(200) DEFAULT NULL,
  `pdf_path` varchar(200) DEFAULT NULL,
  `isdisplay` varchar(20) DEFAULT NULL,
  `isupfile` varchar(20) DEFAULT NULL,
  `state` varchar(20) DEFAULT NULL,
  `pdfname` varchar(200) DEFAULT NULL,
  `intor` text,
  `psize` varchar(20) DEFAULT NULL,
  `sindex` int(32) DEFAULT NULL,
  `downcount` int(32) DEFAULT NULL,
  `addtime` datetime DEFAULT NULL,
  `source` varchar(32) DEFAULT NULL,
  PRIMARY KEY (`pid`)
) ENGINE=InnoDB AUTO_INCREMENT=65 DEFAULT CHARSET=utf8;

