-- ----------------------------
-- Table structure for news_photos
-- ----------------------------
DROP TABLE IF EXISTS `news_photos`;
CREATE TABLE `news_photos` (
  `pid` bigint(32) NOT NULL,
  `nid` bigint(32) DEFAULT NULL,
  `ptitle` varchar(200) DEFAULT NULL,
  `img_path` varchar(200) DEFAULT NULL,
  `psize` varchar(10) DEFAULT NULL,
  `sindex` int(11) DEFAULT NULL,
  `downcount` int(11) DEFAULT NULL,
  `uptime` datetime DEFAULT NULL,
  PRIMARY KEY (`pid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

