-- ----------------------------
-- Table structure for photos
-- ----------------------------
DROP TABLE IF EXISTS `photos`;
CREATE TABLE `photos` (
  `pid` int(11) NOT NULL AUTO_INCREMENT,
  `aid` int(11) DEFAULT NULL,
  `ptitle` varchar(200) DEFAULT NULL,
  `psize` varchar(10) DEFAULT NULL,
  `sindex` int(11) DEFAULT NULL,
  `downcount` int(11) DEFAULT NULL,
  `uptime` datetime DEFAULT NULL,
  PRIMARY KEY (`pid`),
  KEY `FK_PH_AR` (`aid`),
  CONSTRAINT `photos_ibfk_1` FOREIGN KEY (`aid`) REFERENCES `article` (`aid`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of photos
-- ----------------------------

-- ----------------------------
-- Table structure for products
-- ----------------------------
DROP TABLE IF EXISTS `products`;
CREATE TABLE `products` (
  `pid` int(11) NOT NULL AUTO_INCREMENT COMMENT '产品ID',
  `cid` int(11) NOT NULL COMMENT '产品类别ID(外键)',
  `name` varchar(400) NOT NULL,
  `content` longtext COMMENT '商品描述',
  `cover_img` varchar(200) DEFAULT NULL,
  `viewcount` int(11) NOT NULL COMMENT '浏览次数',
  `ifindex` varchar(1) NOT NULL COMMENT '是否推荐',
  `sindex` int(11) NOT NULL COMMENT '排序索引',
  `intro` longtext,
  `doman` varchar(200) DEFAULT NULL,
  `info` text,
  `introduction` longtext,
  `solutions` longtext,
  `highlights` longtext,
  `pubtime` datetime NOT NULL COMMENT '发布时间',
  `pkeywords` varchar(400) DEFAULT NULL,
  `ptitle` varchar(400) DEFAULT NULL,
  `pdescription` varchar(400) DEFAULT NULL,
  PRIMARY KEY (`pid`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of products
-- ----------------------------

-- ----------------------------
-- Table structure for smslogs
-- ----------------------------
DROP TABLE IF EXISTS `smslogs`;
CREATE TABLE `smslogs` (
  `rid` int(11) NOT NULL AUTO_INCREMENT,
  `phone` varchar(15) NOT NULL,
  `vcode` varchar(6) DEFAULT NULL,
  `sendtime` datetime NOT NULL,
  PRIMARY KEY (`rid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of smslogs
-- ----------------------------

-- ----------------------------
-- Table structure for sysusers
-- ----------------------------
DROP TABLE IF EXISTS `sysusers`;
CREATE TABLE `sysusers` (
  `sid` int(11) NOT NULL AUTO_INCREMENT,
  `stype` varchar(9) NOT NULL,
  `uname` varchar(20) NOT NULL,
  `pwd` varchar(32) NOT NULL,
  PRIMARY KEY (`sid`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;

