-- ----------------------------
-- Table structure for vendor
-- ----------------------------
DROP TABLE IF EXISTS `vendor`;
CREATE TABLE `vendor` (
  `vid` int(32) NOT NULL,
  `cid` int(32) NOT NULL,
  `order` varchar(20) DEFAULT NULL COMMENT '手动输入的编号',
  `name` varchar(200) DEFAULT NULL,
  `isview` varchar(1) DEFAULT NULL,
  `introduction` longtext,
  `solutions` longtext,
  `highlights` longtext,
  `sindex` int(11) DEFAULT NULL,
  `ptitle` varchar(200) DEFAULT NULL,
  `pkeywords` varchar(200) DEFAULT NULL,
  `pdescription` varchar(400) DEFAULT NULL,
  PRIMARY KEY (`vid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of vendor
-- ----------------------------

-- ----------------------------
-- Table structure for videos
-- ----------------------------
DROP TABLE IF EXISTS `videos`;
CREATE TABLE `videos` (
  `vid` int(32) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) NOT NULL,
  `vurl` varchar(500) NOT NULL,
  `cover_img` varchar(200) DEFAULT NULL,
  `needcount` int(11) NOT NULL,
  `memo` longtext,
  `sindex` int(11) NOT NULL,
  `viewcount` int(11) NOT NULL,
  `addtime` varchar(32) NOT NULL,
  PRIMARY KEY (`vid`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of videos
-- ----------------------------
INSERT INTO `videos` VALUES ('2', 'PMC系列之柔性生产计划与物料控制(1)1', 'http://my.polyv.net/front/video/preview?vid=903fbe59178b3bc854887dc2040157f9_9', 'http://127.0.0.1:8888/upload/images/201910/20191007_8ce7c18f8a604385851aed1cb9c4eaa5.jpg', '2', '<p class=\"MsoListParagraph\" style=\"margin-left: 21.0pt; text-indent: -21.0pt;\"><strong><span style=\"background-color: #cc33e5; font-size: 14px;\">演讲人：</span></strong><span style=\"text-indent: -21pt; font-size: 12px;\">&nbsp;张老师2</span></p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 21.0pt; text-indent: -21.0pt;\"><strong><span style=\"background-color: #cc33e5;\">内容大纲：</span></strong></p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 21.0pt; text-indent: -21.0pt;\"><span style=\"font-size: 12px; text-indent: -21pt;\">1. MRP系统在总的生产计划中的地位</span></p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 21.0pt; text-indent: -21.0pt;\">&nbsp;</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 21.0pt; text-indent: -21.0pt;\">2. 需求数量的制定</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 21.0pt; text-indent: -21.0pt;\">3. Norman Gaither (葛泽尔)理论</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 21.0pt; text-indent: -21.0pt;\">4. 生产模式的分类</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 42.0pt; text-indent: -21.0pt;\">（1）&nbsp; &nbsp;备货型生产（MTS）</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 42.0pt; text-indent: -21.0pt;\">（2）&nbsp; &nbsp;订货生产方式（MTO）</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 42.0pt; text-indent: -21.0pt;\">（3）&nbsp; &nbsp;混合型生产（MTS+MTO）</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 42.0pt; text-indent: -21.0pt;\">（4）&nbsp; &nbsp;按订单设计生产（ETO)</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 21.0pt; text-indent: -21.0pt;\">5. 数据环境</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 42.0pt; text-indent: -21.0pt;\">（1）&nbsp; 物料主文件3</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 42.0pt; text-indent: -21.0pt;\">（2）&nbsp; BOM的结构</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 42.0pt; text-indent: -21.0pt;\">（3）&nbsp; 工艺路线的作用</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 42.0pt; text-indent: -21.0pt;\">（4）&nbsp; 生产周期提前期关系图</p>\n<p class=\"MsoListParagraph\" style=\"margin-left: 42.0pt; text-indent: -21.0pt;\"><img class=\"wscnph\" src=\"http://127.0.0.1:8888/upload/editor/201910/20191007_cf9f0723e7b44a74bca9e7d2f6542b08.jpg\" /></p>', '1', '6', '2018-09-10 17:45:33');
INSERT INTO `videos` VALUES ('3', 'PMC系列之柔性生产计划与物料控制(2)', 'https://my.polyv.net/front/video/preview?vid=903fbe591774b2f7a080d2c9308902f1_9', null, '1', '<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;\" class=\"MsoListParagraph\">\r\n	<strong><span style=\"background-color:#cc33e5;font-size:14px;\">演讲人：</span></strong><span style=\"text-indent:-21pt;font-size:12px;\">&nbsp;张老师</span> \r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;\" class=\"MsoListParagraph\">\r\n	<strong><span style=\"background-color:#cc33e5;\">内容大纲：</span></strong> \r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;\" class=\"MsoListParagraph\">\r\n	<span style=\"text-indent:-21pt;font-size:12px;\">1. MRP系统在总的生产计划中的地位</span> \r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;\" class=\"MsoListParagraph\">\r\n	2. 需求数量的制定\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;\" class=\"MsoListParagraph\">\r\n	3. Norman Gaither (葛泽尔)理论\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;\" class=\"MsoListParagraph\">\r\n	4. 生产模式的分类\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:42pt;\" class=\"MsoListParagraph\">\r\n	（1）&nbsp; &nbsp;备货型生产（MTS）\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:42pt;\" class=\"MsoListParagraph\">\r\n	（2）&nbsp; &nbsp;订货生产方式（MTO）\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:42pt;\" class=\"MsoListParagraph\">\r\n	（3）&nbsp; &nbsp;混合型生产（MTS+MTO）\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:42pt;\" class=\"MsoListParagraph\">\r\n	（4）&nbsp; &nbsp;按订单设计生产（ETO)\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;\" class=\"MsoListParagraph\">\r\n	5. 数据环境\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:42pt;\" class=\"MsoListParagraph\">\r\n	（1）&nbsp; 物料主文件\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:42pt;\" class=\"MsoListParagraph\">\r\n	（2）&nbsp; BOM的结构\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:42pt;\" class=\"MsoListParagraph\">\r\n	（3）&nbsp; 工艺路线的作用\r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:42pt;\" class=\"MsoListParagraph\">\r\n	（4）&nbsp; 生产周期提前期关系图\r\n</p>', '3', '3', '2018-09-10 17:46:04');
INSERT INTO `videos` VALUES ('4', 'PMC系列之产能负荷计划（1）', 'http://my.polyv.net/front/video/preview?vid=903fbe591746052d73d7ce0c507b485f_9', null, '1', '<p>\r\n	<strong><span style=\"background-color:#EE33EE;\">主讲人：</span></strong>&nbsp;张老师\r\n</p>\r\n<p>\r\n	<strong><span style=\"background-color:#EE33EE;\">内容大纲：&nbsp;</span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21.0pt;text-indent:-21.0pt;\">\r\n	1.&nbsp; 产能影响因素<span></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:42.0pt;text-indent:-21.0pt;\">\r\n	（1）&nbsp; 产能规划\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:42.0pt;text-indent:-21.0pt;\">\r\n	（2）&nbsp; 机器负荷\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21.0pt;text-indent:-21.0pt;\">\r\n	2.&nbsp; 生产计划与进度控制<span></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21.0pt;text-indent:-21.0pt;\">\r\n	3.&nbsp; 案例：生产能力分析<span></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21.0pt;text-indent:-21.0pt;\">\r\n	4.&nbsp; 案例：如何进行产能规划和排产\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"text-indent:-28px;margin-left:21pt;\">\r\n	<span style=\"text-indent:0cm;font-size:12px;\">&nbsp; &nbsp; &nbsp;案例：设备产能规划</span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21.0pt;text-indent:0cm;\">\r\n	<span></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21.0pt;text-indent:-21.0pt;\">\r\n	5.&nbsp; OEE的计算<span></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21.0pt;text-indent:-21.0pt;\">\r\n	6.&nbsp; 案例：设备综合利用率<span></span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21.0pt;text-indent:-21.0pt;\">\r\n	7.&nbsp; 产能规划改善的<span>6</span>大方法\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '3', '4', '2018-09-10 17:49:33');
INSERT INTO `videos` VALUES ('5', 'PMC系列之产能负荷计划（2）', 'http://my.polyv.net/front/video/preview?vid=903fbe5917e832b027a4f0b285e5754c_9', null, '1', '<p style=\"white-space:normal;\">\r\n	<strong><span style=\"background-color:#EE33EE;\">主讲人：</span></strong>&nbsp;张老师\r\n</p>\r\n<p style=\"white-space:normal;\">\r\n	<strong><span style=\"background-color:#EE33EE;\">内容大纲：&nbsp;</span></strong> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;white-space:normal;text-indent:-21pt;\">\r\n	1.&nbsp; 产能影响因素\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:42pt;white-space:normal;text-indent:-21pt;\">\r\n	（1）&nbsp; 产能规划\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:42pt;white-space:normal;text-indent:-21pt;\">\r\n	（2）&nbsp; 机器负荷\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;white-space:normal;text-indent:-21pt;\">\r\n	2.&nbsp; 生产计划与进度控制\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;white-space:normal;text-indent:-21pt;\">\r\n	3.&nbsp; 案例：生产能力分析\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;white-space:normal;text-indent:-21pt;\">\r\n	4.&nbsp; 案例：如何进行产能规划和排产\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;white-space:normal;text-indent:-28px;\">\r\n	<span style=\"text-indent:0cm;font-size:12px;\">&nbsp; &nbsp; &nbsp;案例：设备产能规划</span> \r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;white-space:normal;text-indent:-21pt;\">\r\n	5.&nbsp; OEE的计算\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;white-space:normal;text-indent:-21pt;\">\r\n	6.&nbsp; 案例：设备综合利用率\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;white-space:normal;text-indent:-21pt;\">\r\n	7.&nbsp; 产能规划改善的6大方法\r\n</p>', '4', '5', '2018-09-10 17:49:48');
INSERT INTO `videos` VALUES ('6', '决胜未来 - 供应链价值链再造（1）', 'http://my.polyv.net/front/video/preview?vid=903fbe59179963e0497d208a50e17e18_9', null, '1', '<p class=\"MsoListParagraph\" style=\"margin-left:21pt;font-size:13.3333px;white-space:normal;text-indent:-21pt;\">\r\n	<strong><span style=\"background-color:#CC33E5;font-size:14px;\">演讲人：</span></strong><span style=\"text-indent:-21pt;font-size:12px;\">&nbsp;杨老师</span>\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;font-size:13.3333px;white-space:normal;text-indent:-21pt;\">\r\n	<strong><span style=\"background-color:#CC33E5;\">内容大纲：</span></strong>\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;font-size:13.3333px;white-space:normal;text-indent:-21pt;\">\r\n	<span>1. 从来伊份2018半年报说起</span>\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;font-size:13.3333px;white-space:normal;text-indent:-21pt;\">\r\n	<span>2. 现代企业核心竞争力有那些</span>\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;font-size:13.3333px;white-space:normal;text-indent:-21pt;\">\r\n	<span>3. 供应链价值链</span>\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:21pt;font-size:13.3333px;white-space:normal;text-indent:-21pt;\">\r\n	<span>4. 如何规划企业供应链<br />\r\n</span>\r\n</p>', '5', '7', '2018-09-12 09:44:17');
INSERT INTO `videos` VALUES ('7', '决胜未来 - 供应链价值链再造（2）', 'https://my.polyv.net/front/video/preview?vid=903fbe5917eff2e965fdb4dfb4d69151_9', null, '1', '<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;font-size:13px;\" class=\"MsoListParagraph\">\r\n	<strong><span style=\"background-color:#cc33e5;font-size:14px;\">演讲人：</span></strong><span style=\"text-indent:-21pt;font-size:12px;\">&nbsp;杨老师</span> \r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;font-size:13px;\" class=\"MsoListParagraph\">\r\n	<strong><span style=\"background-color:#cc33e5;\">内容大纲：</span></strong> \r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;font-size:13px;\" class=\"MsoListParagraph\">\r\n	<span>1. 从来伊份2018半年报说起</span> \r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;font-size:13px;\" class=\"MsoListParagraph\">\r\n	<span>2. 现代企业核心竞争力有那些</span> \r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;font-size:13px;\" class=\"MsoListParagraph\">\r\n	<span>3. 供应链价值链</span> \r\n</p>\r\n<p style=\"text-indent:-21pt;white-space:normal;margin-left:21pt;font-size:13px;\" class=\"MsoListParagraph\">\r\n	<span>4. 如何规划企业供应链</span>\r\n</p>', '6', '10', '2018-09-12 09:52:49');

