-- ----------------------------
-- Table structure for worker
-- ----------------------------
DROP TABLE IF EXISTS `worker`;
CREATE TABLE `worker` (
  `wid` int(11) NOT NULL AUTO_INCREMENT,
  `wname` varchar(10) DEFAULT NULL,
  `wtype` int(11) DEFAULT NULL,
  `widcard` varchar(20) DEFAULT NULL,
  `wsex` varchar(1) DEFAULT NULL,
  `wphone` varchar(36) DEFAULT NULL,
  `wtel` varchar(15) DEFAULT NULL,
  `wemail` varchar(100) DEFAULT NULL,
  `wstate` int(11) DEFAULT NULL,
  `wmemo` longtext,
  `path` varchar(400) DEFAULT NULL,
  `pubtime` datetime DEFAULT NULL,
  PRIMARY KEY (`wid`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

