mapper.select:
select nid, ntype, title, author, sindex, ifindex, viewcount, pubtime, cover_img, author_img from news
ntype = #{ntype}
and title like concat('%',#{title},'%')

select n.nid, n.ntype, n.title, n.author, n.content, n.cover_img, n.author_img, n.sindex, n.ifindex, n.viewcount, n.cover_img, n.author_img, n.ptitle, n.pdescription, n.pkeywords, n.pubtime,
e.nlable, e.ndigest, e.endate, e.province, e.city, e.video, e.txt1, e.txt2, e.txt3, e.txt4, e.txt5, e.txt6 from news as n left join news_ext as e on n.nid = e.nid
n.nid = #{nid}
